import {useState, useEffect} from 'react';
import styled from 'styled-components';
import Container from '../components/Container';
import {frontEnd} from '../techData.js';

export default function TechStack() {


  return(
    <>
    <h1 style={{alignSelf: 'start', justifySelf:'start'}}>Tech Stack:</h1>
    <Container bg={[3,4]}>
      <CardContainer>
        {frontEnd.map((data, index) => (
          <Card>
            <Logo className="logoVector" name={data.name}/>
            <h3>{data.name}</h3>
          </Card>
        ))}
      </CardContainer>
    </Container>
    </>
  )
};

const CardContainer = styled.div`
  display: flex;
  align-items:center;
  justify-content:center;
`;

const Card = styled.div`
  background: white;
  color:black;
  margin: auto 1em;
  padding:0.7em;
  border-radius: 10%;
  text-align: center;
  transition: all 1s;

  h3{
    padding:0;
    margin:0;
  }
  :hover{
    .logoVector{
      width: 8em;
      height:8em;
    }
  }
`;

const Logo = styled.div`
  ${({name}) => `background-image: url(./logos/${name}.svg);`}
  background-repeat: no-repeat;
  background-size: 100% 100%;
  transition: all 0.5s;

  width:7em;
  height:7em;
`;
