import {useState, useEffect} from 'react';
import styled from 'styled-components';
import Container from '../components/Container';
import {frontEnd} from '../techData.js';

export default function TechStack() {


  return(
    <Container bg={[3,4]}>
      <h1 style={{alignSelf: 'start'}}>Tech Stack:</h1>
      <CardContainer>
        {frontEnd.map((data, index) => (
          <Card>
            <Logo className="logoVector" name={data.name}/>
            <h3>{data.name}</h3>
          </Card>
        ))}
      </CardContainer>
    </Container>
  )
};
const CardContainer = styled.div`
  display: flex;
  padding: 3em 0;
`;

const Card = styled.div`
  background: white;
  color:black;
  margin: 0 1em;
  padding:0.7em;
  border-radius: 10%;
  text-align: center;
  transition: all 1s;

  h3{
    padding:0;
    margin:0;
  }
  :hover{
    div{
      width: 8em;
      height:8em;
    }
  }
`;

const Logo = styled.div`
  ${({name}) => `background-image: url(./logos/${name}.svg);`}
  background-repeat: no-repeat;
  background-size: 100% 100%;
  transition: all 1s;

  width:7em;
  height:7em;
`;
