import './App.css';
import styled from 'styled-components';
import {useState} from 'react';

function App() {
  const [colors, setColors] = useState({
    dark: '',
    darker: '',
    light: '',
    lighter: '',
  });

  const inputHandler = (e) => {
      e.preventDefault();
      setColors({...colors, [e.target.name]: e.target.value});
  };

  const submitColors = (e) => {
    e.preventDefault();
    let errors = [];

    Object.entries(colors).forEach((entry) => {
      const [key, val] = entry;
      if (!val.match(/^#(?:[0-9a-fA-F]{3}){1,2}$/)) {
        errors.push(key);
      }
    });

    if (errors.length) {
      alert(`${errors.join(', ')} need(s) a valid hexadecimal color!`);
      return;
    }

    //dispatch the colors
  }

  return (
    <div className="App">
      <header className="App-header">
        <Title>
          Jordan Moore
        </Title>
        <ColorForm>
          <h3>Dark: </h3>
          <input type="text" name="dark" onChange={inputHandler}/>
          <h3>Darker: </h3>
          <input type="text" name="darker" onChange={inputHandler}/>
          <h3>Light: </h3>
          <input type="text" name="light" onChange={inputHandler}/>
          <h3>Lighter: </h3>
          <input type="text" name="lighter" onChange={inputHandler}/>
          <button onClick={submitColors}>Change Colors!</button>
        </ColorForm>
      </header>
    </div>
  );
}

export default App;

const Title = styled.div`
  padding: 1em;
  display: flex;
  color: var(--light);
  font-size: 500%;
  letter-spacing: normal;
  transition: all 1s;

  :hover {
    letter-spacing: .05em;
  }
`;

const ColorForm = styled.div`
  color: var(--lighter);
  display: flex;
  flex-flow: wrap column;
  align-items: flex-start;

  button{
    margin: 2em 0;
  }
`;
